using Microsoft.AspNetCore.Mvc;\nusing DeliveryService.Application.Interfaces;\nusing DeliveryService.DTOs;\nusing Shared.Controllers;\nusing Shared.Models;\n\nnamespace DeliveryService.Controllers;\n\n[ApiVersion(\"1.0\")]\n[Route(\"api/v{version:apiVersion}/[controller]\")]\npublic class HubsController : BaseController\n{\n    private readonly IHubAppService _hubAppService;\n\n    public HubsController(IHubAppService hubAppService)\n    {\n        _hubAppService = hubAppService;\n    }\n\n    [HttpGet]\n    public async Task<IActionResult> GetHubs([FromQuery] HubQuery query)\n    {\n        var result = await _hubAppService.GetHubsAsync(query);\n        return Success(result);\n    }\n\n    [HttpGet(\"{id}\")]\n    public async Task<IActionResult> GetHub(string id)\n    {\n        var result = await _hubAppService.GetHubByIdAsync(id);\n        return result != null ? Success(result) : NotFound(\"Hub not found\");\n    }\n\n    [HttpPost]\n    public async Task<IActionResult> CreateHub([FromBody] CreateHubRequest request)\n    {\n        var result = await _hubAppService.CreateHubAsync(request);\n        return Created(result);\n    }\n\n    [HttpPut(\"{id}\")]\n    public async Task<IActionResult> UpdateHub(string id, [FromBody] UpdateHubRequest request)\n    {\n        var result = await _hubAppService.UpdateHubAsync(id, request);\n        return result != null ? Success(result) : NotFound(\"Hub not found\");\n    }\n\n    [HttpGet(\"{id}/capacity\")]\n    public async Task<IActionResult> GetHubCapacity(string id)\n    {\n        var result = await _hubAppService.GetHubCapacityAsync(id);\n        return result != null ? Success(result) : NotFound(\"Hub not found\");\n    }\n\n    [HttpGet(\"{id}/items\")]\n    public async Task<IActionResult> GetHubItems(string id, [FromQuery] ItemQuery query)\n    {\n        query.HubId = id;\n        var result = await _hubAppService.GetHubItemsAsync(query);\n        return Success(result);\n    }\n\n    [HttpGet(\"by-state/{state}\")]\n    public async Task<IActionResult> GetHubsByState(string state)\n    {\n        var result = await _hubAppService.GetHubsByStateAsync(state);\n        return Success(result);\n    }\n}